public class Switchtest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 10; //declaring a variable to switch
		// Switch expression
		switch(num) {
		// case statement
		case 1:System.out.println("12");
		break;
		case 2:System.out.println("15");
		break;
		case 10:System.out.println("very good");
		break;
		case 4:System.out.println("20");
		break;
		// default statement
		default: System.out.println("Number is not within the range");
		}
	}

}