public class Switchvowel {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char letter = 'e'; //declaring a variable to switch
		// Switch expression
		switch(letter) {
		// case statement
		case 'a':System.out.println("Yes 'a' is a vowel");
		break;
		case 'e':System.out.println("Yes 'e' is a vowel");
		break;
		case 'i':System.out.println("Yes 'i' is a vowel");
		break;
		case 'o':System.out.println("Yes 'o' is a vowel");
		break;
		case 'u':System.out.println("Yes 'u' is a vowel");
		break;
		// default statement
		default: System.out.println("Consonent");
		}
	}

}
